import React, { Component } from 'react'

export default class Forms extends Component {
    constructor() {
        super()
        this.state = {
            user: null,
            password: null,
            nameError: null,
            passwordError:null

        }
    }
    valid() {
        if (!this.state.user.includes("@") && this.state.password.length<5) {
            this.setState({ nameError: 'Invalid Name' ,passwordError:'length mast been 5 charecter'})
        }
        else if(this.state.includes("@"))
        {
            this.setState({passwordError:"Invalid Password"})
        }
    }
    submit() {
        if (this.valid()) {
            alert('form has been submited');
            console.log(this.state);
        }
    }
    render() {
        return (
            <div>
                <h1>Handling</h1>
                UserName :
                <input
                    type="text"
                    name="user"
                    onChange={(e) => { this.setState({ user: e.target.value }) }}
                />
                <p style={{ color: "red", fontSize: "12px" }}>{this.state.nameError}</p>
                <br /><br />
                password :
                <input
                    type="password"
                    name="password"
                    onChange={(e) => { this.setState({ password: e.target.value }) }}
                />
                <p style={{color:"red",fontSize:"12px"}}>{this.state.passwordError}</p>
                <br /><br />
                <button onClick={() => this.submit()}> Submit </button>
            </div>
        )
    }
}
