import React, { Component } from "react";

export default class UseRefs extends Component {
  constructor() {
    super();
    this.userRef = React.createRef();
  }
  Ref() {
    this.userRef.current.focus();
  }
  render() {
    return (
      <div>
        <p style="te">User Refs</p>
        <input ref={this.userRef} type="text" />
        <button
          onClick={() => {
            this.Ref();
          }}
        >
          Clik
        </button>
      </div>
    );
  }
}
