import React, { Component } from 'react'
import {Button,model} from 'react-bootstrap'

export default class modal extends Component {
    constructor(){
        super()
        this.state={
            show:false
        }
    }
    handelModel(){
        this.setState({show:true})
    }
    render() {

        return (
            <div>
            <button onClick={()=>{this.handelModel}}>Click me</button>

            <modal show={true}>
                <modal.Header>Moda Header</modal.Header>
                <modal.Body>modal Body</modal.Body>
                <modal.Footer>
                    <Button>Close</Button>
                    <Button>Save</Button>

                </modal.Footer>
            </modal>
            </div>
        )
    }
}
