    }
    submit(){
        console.log(this.state);
    }
    render() {
        return (
            <div>
            <h1>Handling</h1> 
            UserName :  
            <input 
            type="text" 
            name="user"
                onChange={(e)=>{this.setState({user:e.target.value})}}
            />
             <br/><br/>
             password :
            <input type = "password" name="password"
                onChange={(e)}
            />
            <br/><br/>
            <button onClick={this.submit()}> Submit </button>
           </div>
        )
    }
}
