import React, { Component } from "react";

export default class UncontrolledComponent extends Component {
    constructor(){
        super()
        this.name=React.createRef();
        this.password=React.createRef();
    }
    useRef(event){
        event.preventDefault()

        if( this.name.current.focus()){

            console.warn(this.name.current.value)
        }else{
            console.warn(this);
        }

    }
  render() {
    return (
      <div>
        <form onSubmit={(event)=>{this.useRef(event)}}>
            <h1>uncontrolled component</h1>
            <input type="text" placeholder="user name" ref={this.name}/><br/><br/>
            <input type="password" placeholder="user password" ref={this.password}/><br/><br/>
            <button type="submit"> submit</button>

        </form>
      </div>
    );
  }
}
